		$jsReadOnly .= "\tjQuery('#token').prop('disabled', true).css({ color: '#555', backgroundColor: '#fff' });\n";
		$jsReadOnly .= "\tjQuery('#token_caption').prop('disabled', true).css({ color: '#555', backgroundColor: 'white' });\n";
