		$jsReadOnly .= "\tjQuery('#race').replaceWith('<div class=\"form-control-static\" id=\"race\">' + (jQuery('#race').val() || '') + '</div>');\n";
		$jsReadOnly .= "\tjQuery('#age').replaceWith('<div class=\"form-control-static\" id=\"age\">' + (jQuery('#age').val() || '') + '</div>');\n";
		$jsReadOnly .= "\tjQuery('.select2-container').hide();\n";

		$noUploads = true;
	} elseif($AllowInsert) {
		$jsEditable .= "\tjQuery('form').eq(0).data('already_changed', true);"; // temporarily disable form change handler
			$jsEditable .= "\tjQuery('form').eq(0).data('already_changed', false);"; // re-enable form change handler
	}

	// process combos
	$templateCode = str_replace('<%%COMBO(author)%%>', $combo_author->HTML, $templateCode);
	$templateCode = str_replace('<%%COMBOTEXT(author)%%>', $combo_author->MatchText, $templateCode);
