				<?php echo (!$AllowUpdate || $dvprint ? 'if(typeof(transcript_reload__RAND__) == \'function\') transcript_reload__RAND__(AppGini.current_author_id__RAND__.value, AppGini.current_bibliography__RAND__.value);' : ''); ?>
