		echo StyleSheet() . "\n\n<div class=\"alert alert-danger\">" . $Translation['error:'] . " 'IP rights': " . $Translation['field not null'] . '<br><br>';
		echo '<a href="" onclick="history.go(-1); return false;">'.$Translation['< back'].'</a></div>';
		exit;
	}

	/* for empty upload fields, when saving a copy of an existing record, copy the original upload field */
	if($_REQUEST['SelectedID']){
		$res = sql("select * from biblio_transcript where id='" . makeSafe($_REQUEST['SelectedID']) . "'", $eo);
		if($row = db_fetch_assoc($res)){
			if(!$data['transcript']) $data['transcript'] = makeSafe($row['transcript']);
		}
	}

	// hook: biblio_transcript_before_insert
	if(function_exists('biblio_transcript_before_insert')){
		$args=array();
		if(!biblio_transcript_before_insert($data, getMemberInfo(), $args)){ return false; }
	}

	$o = array('silentErrors' => true);
	sql('insert into `biblio_transcript` set       `author`=' . (($data['author'] !== '' && $data['author'] !== NULL) ? "'{$data['author']}'" : 'NULL') . ', `author_memberID`=' . (($data['author_memberID'] !== '' && $data['author_memberID'] !== NULL) ? "'{$data['author_memberID']}'" : 'NULL') . ', `bibliography_id`=' . (($data['bibliography_id'] !== '' && $data['bibliography_id'] !== NULL) ? "'{$data['bibliography_id']}'" : 'NULL') . ', `bibliography_title`=' . (($data['bibliography_title'] !== '' && $data['bibliography_title'] !== NULL) ? "'{$data['bibliography_title']}'" : 'NULL') . ', `trascriber_memberID`=' . (($data['trascriber_memberID'] !== '' && $data['trascriber_memberID'] !== NULL) ? "'{$data['trascriber_memberID']}'" : 'NULL') . ', `transcript_title`=' . (($data['transcript_title'] !== '' && $data['transcript_title'] !== NULL) ? "'{$data['transcript_title']}'" : 'NULL') . ', ' . ($data['transcript'] != '' ? "`transcript`='{$data['transcript']}'" : '`transcript`=NULL') . ', `credits`=' . (($data['credits'] !== '' && $data['credits'] !== NULL) ? "'{$data['credits']}'" : 'NULL') . ', `ip_rights`=' . (($data['ip_rights'] !== '' && $data['ip_rights'] !== NULL) ? "'{$data['ip_rights']}'" : 'NULL'), $o);
