function biblio_transcript_update($selected_id){
	global $Translation;

	// mm: can member edit record?
	$arrPerm=getTablePermissions('biblio_transcript');
	$ownerGroupID=sqlValue("select groupID from membership_userrecords where tableName='biblio_transcript' and pkValue='".makeSafe($selected_id)."'");
	$ownerMemberID=sqlValue("select lcase(memberID) from membership_userrecords where tableName='biblio_transcript' and pkValue='".makeSafe($selected_id)."'");
	if(($arrPerm[3]==1 && $ownerMemberID==getLoggedMemberID()) || ($arrPerm[3]==2 && $ownerGroupID==getLoggedGroupID()) || $arrPerm[3]==3){ // allow update?
		// update allowed, so continue ...
	}else{
		return false;
	}

	$data['author'] = makeSafe($_REQUEST['author_memberID']);
		if($data['author'] == empty_lookup_value){ $data['author'] = ''; }
	$data['author_memberID'] = makeSafe($_REQUEST['author_memberID']);
		if($data['author_memberID'] == empty_lookup_value){ $data['author_memberID'] = ''; }
	if($data['author_memberID']==''){
		echo StyleSheet() . "\n\n<div class=\"alert alert-danger\">{$Translation['error:']} 'Author ID': {$Translation['field not null']}<br><br>";
		echo '<a href="" onclick="history.go(-1); return false;">'.$Translation['< back'].'</a></div>';
		exit;
	}
	$data['bibliography_id'] = makeSafe($_REQUEST['bibliography_title']);
		if($data['bibliography_id'] == empty_lookup_value){ $data['bibliography_id'] = ''; }
	$data['bibliography_title'] = makeSafe($_REQUEST['bibliography_title']);
		if($data['bibliography_title'] == empty_lookup_value){ $data['bibliography_title'] = ''; }
	if($data['bibliography_title']==''){
		echo StyleSheet() . "\n\n<div class=\"alert alert-danger\">{$Translation['error:']} 'Bibliography title': {$Translation['field not null']}<br><br>";
		echo '<a href="" onclick="history.go(-1); return false;">'.$Translation['< back'].'</a></div>';
		exit;
	}
	$data['trascriber_memberID'] = makeSafe($_REQUEST['trascriber_memberID']);
		if($data['trascriber_memberID'] == empty_lookup_value){ $data['trascriber_memberID'] = ''; }
	$data['transcript_title'] = makeSafe($_REQUEST['transcript_title']);
		if($data['transcript_title'] == empty_lookup_value){ $data['transcript_title'] = ''; }
	if($data['transcript_title']==''){
		echo StyleSheet() . "\n\n<div class=\"alert alert-danger\">{$Translation['error:']} 'Transcript title': {$Translation['field not null']}<br><br>";
		echo '<a href="" onclick="history.go(-1); return false;">'.$Translation['< back'].'</a></div>';
		exit;
	}
	$data['transcript'] = PrepareUploadedFile('transcript', 5120000, 'txt|doc|docx|docm|odt|pdf|rtf', false, "");
	$existing_transcript = sqlValue("select `transcript` from `biblio_transcript` where `id`='" . makeSafe($selected_id) . "'");
	if($data['transcript'] == '' && !$existing_transcript){
		echo StyleSheet() . "\n\n<div class=\"alert alert-danger\">{$Translation['error:']} 'Transcript': {$Translation['field not null']}<br><br>";
		echo '<a href="" onclick="history.go(-1); return false;">'.$Translation['< back'].'</a></div>';
		exit;
	}
	$data['credits'] = makeSafe($_REQUEST['credits']);
		if($data['credits'] == empty_lookup_value){ $data['credits'] = ''; }
	$data['ip_rights'] = makeSafe($_REQUEST['ip_rights']);
		if($data['ip_rights'] == empty_lookup_value){ $data['ip_rights'] = ''; }
	if($data['ip_rights']==''){
		echo StyleSheet() . "\n\n<div class=\"alert alert-danger\">{$Translation['error:']} 'IP rights': {$Translation['field not null']}<br><br>";
		echo '<a href="" onclick="history.go(-1); return false;">'.$Translation['< back'].'</a></div>';
		exit;
	}
	$data['selectedID']=makeSafe($selected_id);

	// hook: biblio_transcript_before_update
	if(function_exists('biblio_transcript_before_update')){
		$args=array();
		if(!biblio_transcript_before_update($data, getMemberInfo(), $args)){ return false; }
	}

	$o=array('silentErrors' => true);
	sql('update `biblio_transcript` set       `author`=' . (($data['author'] !== '' && $data['author'] !== NULL) ? "'{$data['author']}'" : 'NULL') . ', `author_memberID`=' . (($data['author_memberID'] !== '' && $data['author_memberID'] !== NULL) ? "'{$data['author_memberID']}'" : 'NULL') . ', `bibliography_id`=' . (($data['bibliography_id'] !== '' && $data['bibliography_id'] !== NULL) ? "'{$data['bibliography_id']}'" : 'NULL') . ', `bibliography_title`=' . (($data['bibliography_title'] !== '' && $data['bibliography_title'] !== NULL) ? "'{$data['bibliography_title']}'" : 'NULL') . ', `trascriber_memberID`=' . (($data['trascriber_memberID'] !== '' && $data['trascriber_memberID'] !== NULL) ? "'{$data['trascriber_memberID']}'" : 'NULL') . ', `transcript_title`=' . (($data['transcript_title'] !== '' && $data['transcript_title'] !== NULL) ? "'{$data['transcript_title']}'" : 'NULL') . ', ' . ($data['transcript']!='' ? "`transcript`='{$data['transcript']}'" : '`transcript`=`transcript`') . ', `credits`=' . (($data['credits'] !== '' && $data['credits'] !== NULL) ? "'{$data['credits']}'" : 'NULL') . ', `ip_rights`=' . (($data['ip_rights'] !== '' && $data['ip_rights'] !== NULL) ? "'{$data['ip_rights']}'" : 'NULL') . " where `id`='".makeSafe($selected_id)."'", $o);
	if($o['error']!=''){
		echo $o['error'];
		echo '<a href="biblio_transcript_view.php?SelectedID='.urlencode($selected_id)."\">{$Translation['< back']}</a>";
		exit;
	}


	// hook: biblio_transcript_after_update
	if(function_exists('biblio_transcript_after_update')){
		$res = sql("SELECT * FROM `biblio_transcript` WHERE `id`='{$data['selectedID']}' LIMIT 1", $eo);
		if($row = db_fetch_assoc($res)){
			$data = array_map('makeSafe', $row);
		}
		$data['selectedID'] = $data['id'];
		$args = array();
		if(!biblio_transcript_after_update($data, getMemberInfo(), $args)){ return; }
	}

	// mm: update ownership data
	sql("update membership_userrecords set dateUpdated='".time()."' where tableName='biblio_transcript' and pkValue='".makeSafe($selected_id)."'", $eo);

}

function biblio_transcript_form($selected_id = '', $AllowUpdate = 1, $AllowInsert = 1, $AllowDelete = 1, $ShowCancel = 0, $TemplateDV = '', $TemplateDVP = ''){
	// function to return an editable form for a table records
	// and fill it with data of record whose ID is $selected_id. If $selected_id
	// is empty, an empty form is shown, with only an 'Add New'
	// button displayed.

	global $Translation;

	// mm: get table permissions
	$arrPerm=getTablePermissions('biblio_transcript');
	if(!$arrPerm[1] && $selected_id==''){ return ''; }
	$AllowInsert = ($arrPerm[1] ? true : false);
	// print preview?
	$dvprint = false;
	if($selected_id && $_REQUEST['dvprint_x'] != ''){
		$dvprint = true;
	}

	$filterer_author_memberID = thisOr(undo_magic_quotes($_REQUEST['filterer_author_memberID']), '');
	$filterer_bibliography_title = thisOr(undo_magic_quotes($_REQUEST['filterer_bibliography_title']), '');
	$filterer_ip_rights = thisOr(undo_magic_quotes($_REQUEST['filterer_ip_rights']), '');

	// populate filterers, starting from children to grand-parents
	if($filterer_bibliography_title && !$filterer_author_memberID) $filterer_author_memberID = sqlValue("select author_id from biblio_doc where id='" . makeSafe($filterer_bibliography_title) . "'");

	// unique random identifier
	$rnd1 = ($dvprint ? rand(1000000, 9999999) : '');
	// combobox: author_memberID
	$combo_author_memberID = new DataCombo;
	// combobox: bibliography_title, filterable by: author_memberID
	$combo_bibliography_title = new DataCombo;
	// combobox: ip_rights
	$combo_ip_rights = new DataCombo;

	if($selected_id){
		// mm: check member permissions
		if(!$arrPerm[2]){
			return "";
		}
		// mm: who is the owner?
		$ownerGroupID=sqlValue("select groupID from membership_userrecords where tableName='biblio_transcript' and pkValue='".makeSafe($selected_id)."'");
		$ownerMemberID=sqlValue("select lcase(memberID) from membership_userrecords where tableName='biblio_transcript' and pkValue='".makeSafe($selected_id)."'");
		if($arrPerm[2]==1 && getLoggedMemberID()!=$ownerMemberID){
			return "";
		}
		if($arrPerm[2]==2 && getLoggedGroupID()!=$ownerGroupID){
			return "";
		}

		// can edit?
