function bio_author_insert(&$error_message = '') {
	global $Translation;

	// mm: can member insert record?
	$arrPerm = getTablePermissions('bio_author');
	if(!$arrPerm['insert']) return false;

	$data = [
		'team' => Request::val('team', ''),
		'author_id' => Request::val('author_id', ''),
		'author_memberid' => Request::lookup('author_id'),
		'last_name' => Request::lookup('author_id'),
		'first_name' => Request::lookup('author_id'),
	];


	// hook: bio_author_before_insert
	if(function_exists('bio_author_before_insert')) {
		$args = [];
		if(!bio_author_before_insert($data, getMemberInfo(), $args)) {
			if(isset($args['error_message'])) $error_message = $args['error_message'];
			return false;
		}
	}

	$error = '';
	// set empty fields to NULL
	$data = array_map(function($v) { return ($v === '' ? NULL : $v); }, $data);
	insert('bio_author', backtick_keys_once($data), $error);
	if($error)
		die("{$error}<br><a href=\"#\" onclick=\"history.go(-1);\">{$Translation['< back']}</a>");

	$recID = db_insert_id(db_link());

	// automatic author_memberid if passed as filterer
	if($_REQUEST['filterer_author_memberid']) {
		sql("UPDATE `bio_author` SET `author_memberid`='" . makeSafe($_REQUEST['filterer_author_memberid']) . "' WHERE `id`='" . makeSafe($recID, false) . "'", $eo);
	}

	update_calc_fields('bio_author', $recID, calculated_fields()['bio_author']);

	// hook: bio_author_after_insert
	if(function_exists('bio_author_after_insert')) {
		$res = sql("SELECT * FROM `bio_author` WHERE `id`='" . makeSafe($recID, false) . "' LIMIT 1", $eo);
		if($row = db_fetch_assoc($res)) {
			$data = array_map('makeSafe', $row);
		}
		$data['selectedID'] = makeSafe($recID, false);
		$args=[];
		if(!bio_author_after_insert($data, getMemberInfo(), $args)) { return $recID; }
	}

	// mm: save ownership data
	set_record_owner('bio_author', $recID, getLoggedMemberID());

	// if this record is a copy of another record, copy children if applicable
	if(!empty($_REQUEST['SelectedID'])) bio_author_copy_children($recID, $_REQUEST['SelectedID']);

	return $recID;
}

function bio_author_copy_children($destination_id, $source_id) {
	global $Translation;
	$requests = []; // array of curl handlers for launching insert requests
	$eo = ['silentErrors' => true];
	$safe_sid = makeSafe($source_id);

	// launch requests, asynchronously
	curl_batch($requests);
}

