function bio_author_update(&$selected_id, &$error_message = '') {
	global $Translation;

	// mm: can member edit record?
	if(!check_record_permission('bio_author', $selected_id, 'edit')) return false;

	$data = [
		'team' => Request::val('team', ''),
		'author_id' => Request::val('author_id', ''),
		'author_memberid' => Request::lookup('author_id'),
		'last_name' => Request::lookup('author_id'),
		'first_name' => Request::lookup('author_id'),
	];

	// get existing values
	$old_data = getRecord('bio_author', $selected_id);
	if(is_array($old_data)) {
		$old_data = array_map('makeSafe', $old_data);
		$old_data['selectedID'] = makeSafe($selected_id);
	}

	$data['selectedID'] = makeSafe($selected_id);

	// hook: bio_author_before_update
	if(function_exists('bio_author_before_update')) {
		$args = ['old_data' => $old_data];
		if(!bio_author_before_update($data, getMemberInfo(), $args)) {
			if(isset($args['error_message'])) $error_message = $args['error_message'];
			return false;
		}
	}

	$set = $data; unset($set['selectedID']);
	foreach ($set as $field => $value) {
		$set[$field] = ($value !== '' && $value !== NULL) ? $value : NULL;
	}

	if(!update(
		'bio_author', 
		backtick_keys_once($set), 
		['`id`' => $selected_id], 
		$error_message
	)) {
		echo $error_message;
		echo '<a href="bio_author_view.php?SelectedID=' . urlencode($selected_id) . "\">{$Translation['< back']}</a>";
		exit;
	}


	$eo = ['silentErrors' => true];

	update_calc_fields('bio_author', $data['selectedID'], calculated_fields()['bio_author']);

	// hook: bio_author_after_update
	if(function_exists('bio_author_after_update')) {
		$res = sql("SELECT * FROM `bio_author` WHERE `id`='{$data['selectedID']}' LIMIT 1", $eo);
		if($row = db_fetch_assoc($res)) $data = array_map('makeSafe', $row);

		$data['selectedID'] = $data['id'];
		$args = ['old_data' => $old_data];
		if(!bio_author_after_update($data, getMemberInfo(), $args)) return;
	}

	// mm: update ownership data
	sql("UPDATE `membership_userrecords` SET `dateUpdated`='" . time() . "' WHERE `tableName`='bio_author' AND `pkValue`='" . makeSafe($selected_id) . "'", $eo);
}

