	// hook: bio_chr_dev_footer
	$footerCode = '';
	if(function_exists('bio_chr_dev_footer')) {
		$args = [];
		$footerCode = bio_chr_dev_footer($x->ContentType, getMemberInfo(), $args);
	}

	if(!$footerCode) {
		include_once("{$currDir}/footer.php"); 
	} else {
		ob_start();
		include_once("{$currDir}/footer.php");
		echo str_replace('<%%FOOTER%%>', ob_get_clean(), $footerCode);
	}
