function bio_chr_insert(&$error_message = '') {
	global $Translation;

	// mm: can member insert record?
	$arrPerm = getTablePermissions('bio_chr');
	if(!$arrPerm['insert']) return false;

	$data = [
		'img' => Request::fileUpload('img', [
			'maxSize' => 5120000,
			'types' => 'jpg|jpeg|gif|png',
			'noRename' => false,
			'dir' => '',
			'success' => function($name, $selected_id) {
				createThumbnail($name, getThumbnailSpecs('bio_chr', 'img', 'tv'));
				createThumbnail($name, getThumbnailSpecs('bio_chr', 'img', 'dv'));
			},
			'failure' => function($selected_id, $fileRemoved) {
				if(!isset($_REQUEST['SelectedID'])) return '';

				/* for empty upload fields, when saving a copy of an existing record, copy the original upload field */
				return existing_value('bio_chr', 'img', $_REQUEST['SelectedID']);
			},
		]),
		'author_id' => Request::val('author_id', ''),
		'author_name' => Request::lookup('author_id'),
		'agent_id' => Request::val('agent_id', ''),
		'agent_name' => Request::lookup('agent_id'),
		'bio_story' => Request::val('bio_story', ''),
		'bio_character' => Request::val('bio_character', ''),
		'bio_archetype' => Request::val('bio_archetype', ''),
		'character_name' => Request::val('character_name', ''),
		'role' => Request::val('role', ''),
		'comment' => Request::val('comment', ''),
	];


	// hook: bio_chr_before_insert
	if(function_exists('bio_chr_before_insert')) {
		$args = [];
		if(!bio_chr_before_insert($data, getMemberInfo(), $args)) {
			if(isset($args['error_message'])) $error_message = $args['error_message'];
			return false;
		}
	}

	$error = '';
	// set empty fields to NULL
	$data = array_map(function($v) { return ($v === '' ? NULL : $v); }, $data);
	insert('bio_chr', backtick_keys_once($data), $error);
	if($error)
		die("{$error}<br><a href=\"#\" onclick=\"history.go(-1);\">{$Translation['< back']}</a>");

	$recID = db_insert_id(db_link());

	update_calc_fields('bio_chr', $recID, calculated_fields()['bio_chr']);

	// hook: bio_chr_after_insert
	if(function_exists('bio_chr_after_insert')) {
		$res = sql("SELECT * FROM `bio_chr` WHERE `id`='" . makeSafe($recID, false) . "' LIMIT 1", $eo);
		if($row = db_fetch_assoc($res)) {
			$data = array_map('makeSafe', $row);
		}
		$data['selectedID'] = makeSafe($recID, false);
		$args=[];
		if(!bio_chr_after_insert($data, getMemberInfo(), $args)) { return $recID; }
	}

	// mm: save ownership data
	set_record_owner('bio_chr', $recID, getLoggedMemberID());

	// if this record is a copy of another record, copy children if applicable
	if(!empty($_REQUEST['SelectedID'])) bio_chr_copy_children($recID, $_REQUEST['SelectedID']);

	return $recID;
}

