function bio_chr_update(&$selected_id, &$error_message = '') {
	global $Translation;

	// mm: can member edit record?
	if(!check_record_permission('bio_chr', $selected_id, 'edit')) return false;

	$data = [
		'img' => Request::fileUpload('img', [
			'maxSize' => 5120000,
			'types' => 'jpg|jpeg|gif|png',
			'noRename' => false,
			'dir' => '',
			'id' => $selected_id,
			'success' => function($name, $selected_id) {
				createThumbnail($name, getThumbnailSpecs('bio_chr', 'img', 'tv'));
				createThumbnail($name, getThumbnailSpecs('bio_chr', 'img', 'dv'));
			},
			'failure' => function($selected_id, $fileRemoved) {
				if($fileRemoved) return '';
				return existing_value('bio_chr', 'img', $selected_id);
			},
		]),
		'author_id' => Request::val('author_id', ''),
		'author_name' => Request::lookup('author_id'),
		'agent_id' => Request::val('agent_id', ''),
		'agent_name' => Request::lookup('agent_id'),
		'bio_story' => Request::val('bio_story', ''),
		'bio_character' => Request::val('bio_character', ''),
		'bio_archetype' => Request::val('bio_archetype', ''),
		'character_name' => Request::val('character_name', ''),
		'role' => Request::val('role', ''),
		'comment' => Request::val('comment', ''),
	];

	// get existing values
	$old_data = getRecord('bio_chr', $selected_id);
	if(is_array($old_data)) {
		$old_data = array_map('makeSafe', $old_data);
		$old_data['selectedID'] = makeSafe($selected_id);
	}

	$data['selectedID'] = makeSafe($selected_id);

	// hook: bio_chr_before_update
	if(function_exists('bio_chr_before_update')) {
		$args = ['old_data' => $old_data];
		if(!bio_chr_before_update($data, getMemberInfo(), $args)) {
			if(isset($args['error_message'])) $error_message = $args['error_message'];
			return false;
		}
	}

	$set = $data; unset($set['selectedID']);
	foreach ($set as $field => $value) {
		$set[$field] = ($value !== '' && $value !== NULL) ? $value : NULL;
	}

	if(!update(
		'bio_chr', 
		backtick_keys_once($set), 
		['`id`' => $selected_id], 
		$error_message
	)) {
		echo $error_message;
		echo '<a href="bio_chr_view.php?SelectedID=' . urlencode($selected_id) . "\">{$Translation['< back']}</a>";
		exit;
	}


	$eo = ['silentErrors' => true];

	update_calc_fields('bio_chr', $data['selectedID'], calculated_fields()['bio_chr']);

	// hook: bio_chr_after_update
	if(function_exists('bio_chr_after_update')) {
		$res = sql("SELECT * FROM `bio_chr` WHERE `id`='{$data['selectedID']}' LIMIT 1", $eo);
		if($row = db_fetch_assoc($res)) $data = array_map('makeSafe', $row);

		$data['selectedID'] = $data['id'];
		$args = ['old_data' => $old_data];
		if(!bio_chr_after_update($data, getMemberInfo(), $args)) return;
	}

	// mm: update ownership data
	sql("UPDATE `membership_userrecords` SET `dateUpdated`='" . time() . "' WHERE `tableName`='bio_chr' AND `pkValue`='" . makeSafe($selected_id) . "'", $eo);
}

function bio_chr_form($selected_id = '', $AllowUpdate = 1, $AllowInsert = 1, $AllowDelete = 1, $ShowCancel = 0, $TemplateDV = '', $TemplateDVP = '') {
	// function to return an editable form for a table records
	// and fill it with data of record whose ID is $selected_id. If $selected_id
	// is empty, an empty form is shown, with only an 'Add New'
	// button displayed.

	global $Translation;

	// mm: get table permissions
	$arrPerm = getTablePermissions('bio_chr');
	if(!$arrPerm['insert'] && $selected_id=='') { return ''; }
	$AllowInsert = ($arrPerm['insert'] ? true : false);
	// print preview?
	$dvprint = false;
	if($selected_id && $_REQUEST['dvprint_x'] != '') {
		$dvprint = true;
	}

	$filterer_author_id = thisOr($_REQUEST['filterer_author_id'], '');
	$filterer_agent_id = thisOr($_REQUEST['filterer_agent_id'], '');
	$filterer_bio_story = thisOr($_REQUEST['filterer_bio_story'], '');
	$filterer_bio_character = thisOr($_REQUEST['filterer_bio_character'], '');
	$filterer_bio_archetype = thisOr($_REQUEST['filterer_bio_archetype'], '');

	// populate filterers, starting from children to grand-parents
	if($filterer_bio_story && !$filterer_author_id) $filterer_author_id = sqlValue("select author_id from bio_story where id='" . makeSafe($filterer_bio_story) . "'");

	// unique random identifier
	$rnd1 = ($dvprint ? rand(1000000, 9999999) : '');
	// combobox: author_id
	$combo_author_id = new DataCombo;
	// combobox: agent_id
	$combo_agent_id = new DataCombo;
	// combobox: bio_story, filterable by: author_id
	$combo_bio_story = new DataCombo;
	// combobox: bio_character
	$combo_bio_character = new DataCombo;
	// combobox: bio_archetype
	$combo_bio_archetype = new DataCombo;

	if($selected_id) {
		// mm: check member permissions
		if(!$arrPerm['view']) return '';

		// mm: who is the owner?
		$ownerGroupID = sqlValue("SELECT `groupID` FROM `membership_userrecords` WHERE `tableName`='bio_chr' AND `pkValue`='" . makeSafe($selected_id) . "'");
		$ownerMemberID = sqlValue("SELECT LCASE(`memberID`) FROM `membership_userrecords` WHERE `tableName`='bio_chr' AND `pkValue`='" . makeSafe($selected_id) . "'");

		if($arrPerm['view'] == 1 && getLoggedMemberID() != $ownerMemberID) return '';
		if($arrPerm['view'] == 2 && getLoggedGroupID() != $ownerGroupID) return '';

		// can edit?
		$AllowUpdate = 0;
		if(($arrPerm['edit'] == 1 && $ownerMemberID == getLoggedMemberID()) || ($arrPerm['edit'] == 2 && $ownerGroupID == getLoggedGroupID()) || $arrPerm['edit'] == 3) {
			$AllowUpdate = 1;
		}

		$res = sql("SELECT * FROM `bio_chr` WHERE `id`='" . makeSafe($selected_id) . "'", $eo);
		if(!($row = db_fetch_array($res))) {
			return error_message($Translation['No records found'], 'bio_chr_view.php', false);
		}
		$combo_author_id->SelectedData = $row['author_id'];
		$combo_agent_id->SelectedData = $row['agent_id'];
