		'invivo_code' => Request::val('invivo_code', ''),
		'startdate' => Request::val('startdate', ''),
		'enddate' => Request::lookup('invivo_code'),
		'person' => Request::lookup('invivo_code'),
		'place' => Request::lookup('invivo_code'),
		'herme_code' => Request::val('herme_code', ''),
		'impression' => Request::lookup('herme_code'),
		'noetictension' => Request::lookup('herme_code'),
		'pc' => Request::lookup('herme_code'),
		'chr_element' => Request::val('chr_element', ''),
		'comment' => br2nl(Request::val('comment', '')),
		'illustration' => br2nl(Request::val('illustration', '')),
		'scene' => br2nl(Request::val('scene', '')),
	];

	// get existing values
	$old_data = getRecord('bio_chr_scene', $selected_id);
	if(is_array($old_data)) {
		$old_data = array_map('makeSafe', $old_data);
		$old_data['selectedID'] = makeSafe($selected_id);
	}

	$data['selectedID'] = makeSafe($selected_id);

	// hook: bio_chr_scene_before_update
	if(function_exists('bio_chr_scene_before_update')) {
		$args = ['old_data' => $old_data];
		if(!bio_chr_scene_before_update($data, getMemberInfo(), $args)) {
			if(isset($args['error_message'])) $error_message = $args['error_message'];
			return false;
		}
	}

	$set = $data; unset($set['selectedID']);
	foreach ($set as $field => $value) {
		$set[$field] = ($value !== '' && $value !== NULL) ? $value : NULL;
	}

	if(!update(
		'bio_chr_scene', 
		backtick_keys_once($set), 
		['`id`' => $selected_id], 
		$error_message
	)) {
		echo $error_message;
		echo '<a href="bio_chr_scene_view.php?SelectedID=' . urlencode($selected_id) . "\">{$Translation['< back']}</a>";
		exit;
	}


	$eo = ['silentErrors' => true];

	update_calc_fields('bio_chr_scene', $data['selectedID'], calculated_fields()['bio_chr_scene']);

	// hook: bio_chr_scene_after_update
	if(function_exists('bio_chr_scene_after_update')) {
		$res = sql("SELECT * FROM `bio_chr_scene` WHERE `id`='{$data['selectedID']}' LIMIT 1", $eo);
		if($row = db_fetch_assoc($res)) $data = array_map('makeSafe', $row);

		$data['selectedID'] = $data['id'];
		$args = ['old_data' => $old_data];
		if(!bio_chr_scene_after_update($data, getMemberInfo(), $args)) return;
	}

	// mm: update ownership data
	sql("UPDATE `membership_userrecords` SET `dateUpdated`='" . time() . "' WHERE `tableName`='bio_chr_scene' AND `pkValue`='" . makeSafe($selected_id) . "'", $eo);
}

