		$x->QueryWhere = "WHERE `bio_chr_scenes`.`id`=`membership_userrecords`.`pkValue` AND `membership_userrecords`.`tableName`='bio_chr_scenes' AND `membership_userrecords`.`groupID`='" . getLoggedGroupID() . "'";
	} elseif($perm['view'] == 3) { // view all
		// no further action
	} elseif($perm['view'] == 0) { // view none
		$x->QueryFields = ['Not enough permissions' => 'NEP'];
		$x->QueryFrom = '`bio_chr_scenes`';
		$x->QueryWhere = '';
		$x->DefaultSortField = '';
	}
	// hook: bio_chr_scenes_init
	$render = true;
	if(function_exists('bio_chr_scenes_init')) {
		$args = [];
		$render = bio_chr_scenes_init($x, getMemberInfo(), $args);
	}

	if($render) $x->Render();

	// hook: bio_chr_scenes_header
	$headerCode = '';
	if(function_exists('bio_chr_scenes_header')) {
		$args = [];
		$headerCode = bio_chr_scenes_header($x->ContentType, getMemberInfo(), $args);
	}

	if(!$headerCode) {
		include_once("{$currDir}/header.php"); 
	} else {
		ob_start();
		include_once("{$currDir}/header.php");
		echo str_replace('<%%HEADER%%>', ob_get_clean(), $headerCode);
	}

	echo $x->HTML;

	// hook: bio_chr_scenes_footer
	$footerCode = '';
