function bio_story_insert(&$error_message = '') {
	global $Translation;

	// mm: can member insert record?
	$arrPerm = getTablePermissions('bio_story');
	if(!$arrPerm['insert']) return false;

	$data = [
		'bio_team' => Request::val('bio_team', ''),
		'author_id' => Request::val('author_id', ''),
		'author_name' => Request::lookup('author_id'),
		'type' => Request::val('type', ''),
		'agent_id' => Request::val('agent_id', ''),
		'agent_name' => Request::lookup('agent_id'),
		'story_title' => Request::val('story_title', ''),
		'approach' => Request::val('approach', ''),
		'tags' => Request::multipleChoice('tags', ''),
		'citation' => br2nl(Request::val('citation', '')),
		'collaboration_status' => Request::val('collaboration_status', ''),
	];


	// hook: bio_story_before_insert
	if(function_exists('bio_story_before_insert')) {
		$args = [];
		if(!bio_story_before_insert($data, getMemberInfo(), $args)) {
			if(isset($args['error_message'])) $error_message = $args['error_message'];
			return false;
		}
	}

	$error = '';
	// set empty fields to NULL
	$data = array_map(function($v) { return ($v === '' ? NULL : $v); }, $data);
	insert('bio_story', backtick_keys_once($data), $error);
	if($error)
		die("{$error}<br><a href=\"#\" onclick=\"history.go(-1);\">{$Translation['< back']}</a>");

	$recID = db_insert_id(db_link());

	update_calc_fields('bio_story', $recID, calculated_fields()['bio_story']);

	// hook: bio_story_after_insert
	if(function_exists('bio_story_after_insert')) {
		$res = sql("SELECT * FROM `bio_story` WHERE `id`='" . makeSafe($recID, false) . "' LIMIT 1", $eo);
		if($row = db_fetch_assoc($res)) {
			$data = array_map('makeSafe', $row);
		}
		$data['selectedID'] = makeSafe($recID, false);
		$args=[];
		if(!bio_story_after_insert($data, getMemberInfo(), $args)) { return $recID; }
	}

	// mm: save ownership data
	set_record_owner('bio_story', $recID, getLoggedMemberID());

	// if this record is a copy of another record, copy children if applicable
	if(!empty($_REQUEST['SelectedID'])) bio_story_copy_children($recID, $_REQUEST['SelectedID']);

	return $recID;
}

