	$data = [
		'bio_team' => Request::val('bio_team', ''),
		'author_id' => Request::val('author_id', ''),
		'author_name' => Request::lookup('author_id'),
		'type' => Request::val('type', ''),
		'agent_id' => Request::val('agent_id', ''),
		'agent_name' => Request::lookup('agent_id'),
		'story_title' => Request::val('story_title', ''),
		'approach' => Request::val('approach', ''),
		'tags' => Request::multipleChoice('tags', ''),
		'citation' => br2nl(Request::val('citation', '')),
		'collaboration_status' => Request::val('collaboration_status', ''),
	];

	// get existing values
	$old_data = getRecord('bio_story', $selected_id);
	if(is_array($old_data)) {
		$old_data = array_map('makeSafe', $old_data);
		$old_data['selectedID'] = makeSafe($selected_id);
	}

	$data['selectedID'] = makeSafe($selected_id);

	// hook: bio_story_before_update
	if(function_exists('bio_story_before_update')) {
		$args = ['old_data' => $old_data];
		if(!bio_story_before_update($data, getMemberInfo(), $args)) {
			if(isset($args['error_message'])) $error_message = $args['error_message'];
			return false;
		}
	}

	$set = $data; unset($set['selectedID']);
	foreach ($set as $field => $value) {
		$set[$field] = ($value !== '' && $value !== NULL) ? $value : NULL;
	}

	if(!update(
		'bio_story', 
		backtick_keys_once($set), 
		['`id`' => $selected_id], 
		$error_message
	)) {
		echo $error_message;
		echo '<a href="bio_story_view.php?SelectedID=' . urlencode($selected_id) . "\">{$Translation['< back']}</a>";
		exit;
	}


	$eo = ['silentErrors' => true];

	update_calc_fields('bio_story', $data['selectedID'], calculated_fields()['bio_story']);

	// hook: bio_story_after_update
	if(function_exists('bio_story_after_update')) {
		$res = sql("SELECT * FROM `bio_story` WHERE `id`='{$data['selectedID']}' LIMIT 1", $eo);
		if($row = db_fetch_assoc($res)) $data = array_map('makeSafe', $row);

		$data['selectedID'] = $data['id'];
		$args = ['old_data' => $old_data];
		if(!bio_story_after_update($data, getMemberInfo(), $args)) return;
	}

	// mm: update ownership data
	sql("UPDATE `membership_userrecords` SET `dateUpdated`='" . time() . "' WHERE `tableName`='bio_story' AND `pkValue`='" . makeSafe($selected_id) . "'", $eo);
}

