		$x->QueryFrom .= ', `membership_userrecords`';
		$x->QueryWhere = "WHERE `bio_storylines`.`id`=`membership_userrecords`.`pkValue` AND `membership_userrecords`.`tableName`='bio_storylines' AND `membership_userrecords`.`groupID`='" . getLoggedGroupID() . "'";
