function bio_storystatic_update(&$selected_id, &$error_message = '') {
	global $Translation;

	// mm: can member edit record?
	if(!check_record_permission('bio_storystatic', $selected_id, 'edit')) return false;

	$data = [
		'story' => Request::val('story', ''),
		'throughline' => Request::val('throughline', ''),
		'story_character_mc' => Request::val('story_character_mc', ''),
		'throughline_domain' => Request::val('throughline_domain', ''),
		'concern' => Request::val('concern', ''),
		'issue' => Request::val('issue', ''),
		'problem' => Request::val('problem', ''),
		'solution' => Request::val('solution', ''),
		'symptom' => Request::val('symptom', ''),
		'response' => Request::val('response', ''),
		'catalyst' => Request::val('catalyst', ''),
		'inhibitor' => Request::val('inhibitor', ''),
		'benchmark' => Request::val('benchmark', ''),
		'signpost1' => Request::val('signpost1', ''),
		'signpost2' => Request::val('signpost2', ''),
		'signpost3' => Request::val('signpost3', ''),
		'signpost4' => Request::val('signpost4', ''),
	];

	// get existing values
	$old_data = getRecord('bio_storystatic', $selected_id);
	if(is_array($old_data)) {
		$old_data = array_map('makeSafe', $old_data);
		$old_data['selectedID'] = makeSafe($selected_id);
	}

	$data['selectedID'] = makeSafe($selected_id);

	// hook: bio_storystatic_before_update
	if(function_exists('bio_storystatic_before_update')) {
		$args = ['old_data' => $old_data];
		if(!bio_storystatic_before_update($data, getMemberInfo(), $args)) {
			if(isset($args['error_message'])) $error_message = $args['error_message'];
			return false;
		}
	}

	$set = $data; unset($set['selectedID']);
	foreach ($set as $field => $value) {
		$set[$field] = ($value !== '' && $value !== NULL) ? $value : NULL;
	}

	if(!update(
		'bio_storystatic', 
		backtick_keys_once($set), 
		['`id`' => $selected_id], 
		$error_message
	)) {
		echo $error_message;
		echo '<a href="bio_storystatic_view.php?SelectedID=' . urlencode($selected_id) . "\">{$Translation['< back']}</a>";
		exit;
	}


	$eo = ['silentErrors' => true];

	update_calc_fields('bio_storystatic', $data['selectedID'], calculated_fields()['bio_storystatic']);

	// hook: bio_storystatic_after_update
	if(function_exists('bio_storystatic_after_update')) {
		$res = sql("SELECT * FROM `bio_storystatic` WHERE `id`='{$data['selectedID']}' LIMIT 1", $eo);
		if($row = db_fetch_assoc($res)) $data = array_map('makeSafe', $row);

		$data['selectedID'] = $data['id'];
		$args = ['old_data' => $old_data];
		if(!bio_storystatic_after_update($data, getMemberInfo(), $args)) return;
	}

	// mm: update ownership data
	sql("UPDATE `membership_userrecords` SET `dateUpdated`='" . time() . "' WHERE `tableName`='bio_storystatic' AND `pkValue`='" . makeSafe($selected_id) . "'", $eo);
}

