function hist_chr_scene_update(&$selected_id, &$error_message = '') {
	global $Translation;

	// mm: can member edit record?
	if(!check_record_permission('hist_chr_scene', $selected_id, 'edit')) return false;

	$data = [
		'author_id' => Request::val('author_id', ''),
		'author_name' => Request::lookup('author_id'),
		'hist_story' => Request::val('hist_story', ''),
		'character' => Request::val('character', ''),
		'agent_id' => Request::val('agent_id', ''),
		'agent_name' => Request::lookup('agent_id'),
		'bio_story' => Request::val('bio_story', ''),
		'bio_storyline_no' => Request::val('bio_storyline_no', ''),
		'bio_storyline_text' => Request::lookup('bio_storyline_no'),
		'chr_element' => Request::val('chr_element', ''),
		'scene' => br2nl(Request::val('scene', '')),
		'illustration' => br2nl(Request::val('illustration', '')),
		'comment' => br2nl(Request::val('comment', '')),
	];

	// get existing values
	$old_data = getRecord('hist_chr_scene', $selected_id);
	if(is_array($old_data)) {
		$old_data = array_map('makeSafe', $old_data);
		$old_data['selectedID'] = makeSafe($selected_id);
	}

	$data['selectedID'] = makeSafe($selected_id);

	// hook: hist_chr_scene_before_update
	if(function_exists('hist_chr_scene_before_update')) {
		$args = ['old_data' => $old_data];
		if(!hist_chr_scene_before_update($data, getMemberInfo(), $args)) {
			if(isset($args['error_message'])) $error_message = $args['error_message'];
			return false;
		}
	}

	$set = $data; unset($set['selectedID']);
	foreach ($set as $field => $value) {
		$set[$field] = ($value !== '' && $value !== NULL) ? $value : NULL;
	}

	if(!update(
		'hist_chr_scene', 
		backtick_keys_once($set), 
		['`id`' => $selected_id], 
		$error_message
	)) {
		echo $error_message;
		echo '<a href="hist_chr_scene_view.php?SelectedID=' . urlencode($selected_id) . "\">{$Translation['< back']}</a>";
		exit;
	}


	$eo = ['silentErrors' => true];

	update_calc_fields('hist_chr_scene', $data['selectedID'], calculated_fields()['hist_chr_scene']);

	// hook: hist_chr_scene_after_update
	if(function_exists('hist_chr_scene_after_update')) {
		$res = sql("SELECT * FROM `hist_chr_scene` WHERE `id`='{$data['selectedID']}' LIMIT 1", $eo);
		if($row = db_fetch_assoc($res)) $data = array_map('makeSafe', $row);

		$data['selectedID'] = $data['id'];
		$args = ['old_data' => $old_data];
		if(!hist_chr_scene_after_update($data, getMemberInfo(), $args)) return;
	}

	// mm: update ownership data
	sql("UPDATE `membership_userrecords` SET `dateUpdated`='" . time() . "' WHERE `tableName`='hist_chr_scene' AND `pkValue`='" . makeSafe($selected_id) . "'", $eo);
