	update_calc_fields('story_chrs', $recID, calculated_fields()['story_chrs']);

	// hook: story_chrs_after_insert
	if(function_exists('story_chrs_after_insert')) {
		$res = sql("SELECT * FROM `story_chrs` WHERE `id`='" . makeSafe($recID, false) . "' LIMIT 1", $eo);
		if($row = db_fetch_assoc($res)) {
			$data = array_map('makeSafe', $row);
		}
		$data['selectedID'] = makeSafe($recID, false);
		$args=[];
		if(!story_chrs_after_insert($data, getMemberInfo(), $args)) { return $recID; }
	}

	// mm: save ownership data
	set_record_owner('story_chrs', $recID, getLoggedMemberID());

	// if this record is a copy of another record, copy children if applicable
	if(!empty($_REQUEST['SelectedID'])) story_chrs_copy_children($recID, $_REQUEST['SelectedID']);

