function story_chrs_update(&$selected_id, &$error_message = '') {
	global $Translation;

	// mm: can member edit record?
	if(!check_record_permission('story_chrs', $selected_id, 'edit')) return false;

	$data = [
		'agent_id' => Request::val('agent_id', ''),
		'agent_name' => Request::lookup('agent_id'),
		'character' => Request::val('character', ''),
		'role' => Request::val('role', ''),
		'story_character' => Request::val('story_character', ''),
		'story' => Request::val('story', ''),
		'story_archetype' => Request::val('story_archetype', ''),
		'comment' => Request::val('comment', ''),
	];

	// get existing values
	$old_data = getRecord('story_chrs', $selected_id);
	if(is_array($old_data)) {
		$old_data = array_map('makeSafe', $old_data);
		$old_data['selectedID'] = makeSafe($selected_id);
	}

	$data['selectedID'] = makeSafe($selected_id);

	// hook: story_chrs_before_update
	if(function_exists('story_chrs_before_update')) {
		$args = ['old_data' => $old_data];
		if(!story_chrs_before_update($data, getMemberInfo(), $args)) {
			if(isset($args['error_message'])) $error_message = $args['error_message'];
			return false;
		}
	}

	$set = $data; unset($set['selectedID']);
	foreach ($set as $field => $value) {
		$set[$field] = ($value !== '' && $value !== NULL) ? $value : NULL;
	}

	if(!update(
		'story_chrs', 
		backtick_keys_once($set), 
		['`id`' => $selected_id], 
		$error_message
	)) {
		echo $error_message;
		echo '<a href="story_chrs_view.php?SelectedID=' . urlencode($selected_id) . "\">{$Translation['< back']}</a>";
		exit;
	}


	$eo = ['silentErrors' => true];

	update_calc_fields('story_chrs', $data['selectedID'], calculated_fields()['story_chrs']);

	// hook: story_chrs_after_update
	if(function_exists('story_chrs_after_update')) {
		$res = sql("SELECT * FROM `story_chrs` WHERE `id`='{$data['selectedID']}' LIMIT 1", $eo);
		if($row = db_fetch_assoc($res)) $data = array_map('makeSafe', $row);

		$data['selectedID'] = $data['id'];
		$args = ['old_data' => $old_data];
		if(!story_chrs_after_update($data, getMemberInfo(), $args)) return;
	}

	// mm: update ownership data
	sql("UPDATE `membership_userrecords` SET `dateUpdated`='" . time() . "' WHERE `tableName`='story_chrs' AND `pkValue`='" . makeSafe($selected_id) . "'", $eo);
}

