	<?php

	$lookups = str_replace('__RAND__', $rnd1, ob_get_contents());
	ob_end_clean();


	// code for template based detail view forms

	// open the detail view template
	if($dvprint) {
		$template_file = is_file("./{$TemplateDVP}") ? "./{$TemplateDVP}" : './templates/story_chrs_templateDVP.html';
		$templateCode = @file_get_contents($template_file);
	} else {
		$template_file = is_file("./{$TemplateDV}") ? "./{$TemplateDV}" : './templates/story_chrs_templateDV.html';
		$templateCode = @file_get_contents($template_file);
	}

	// process form title
	$templateCode = str_replace('<%%DETAIL_VIEW_TITLE%%>', 'Detail View', $templateCode);
	$templateCode = str_replace('<%%RND1%%>', $rnd1, $templateCode);
	$templateCode = str_replace('<%%EMBEDDED%%>', ($_REQUEST['Embedded'] ? 'Embedded=1' : ''), $templateCode);
	// process buttons
	if($AllowInsert) {
		if(!$selected_id) $templateCode = str_replace('<%%INSERT_BUTTON%%>', '<button type="submit" class="btn btn-success" id="insert" name="insert_x" value="1" onclick="return story_chrs_validateData();"><i class="glyphicon glyphicon-plus-sign"></i> ' . $Translation['Save New'] . '</button>', $templateCode);
		$templateCode = str_replace('<%%INSERT_BUTTON%%>', '<button type="submit" class="btn btn-default" id="insert" name="insert_x" value="1" onclick="return story_chrs_validateData();"><i class="glyphicon glyphicon-plus-sign"></i> ' . $Translation['Save As Copy'] . '</button>', $templateCode);
	} else {
		$templateCode = str_replace('<%%INSERT_BUTTON%%>', '', $templateCode);
	}

	// 'Back' button action
	if($_REQUEST['Embedded']) {
		$backAction = 'AppGini.closeParentModal(); return false;';
	} else {
		$backAction = '$j(\'form\').eq(0).attr(\'novalidate\', \'novalidate\'); document.myform.reset(); return true;';
	}

	if($selected_id) {
		if(!$_REQUEST['Embedded']) $templateCode = str_replace('<%%DVPRINT_BUTTON%%>', '<button type="submit" class="btn btn-default" id="dvprint" name="dvprint_x" value="1" onclick="$j(\'form\').eq(0).prop(\'novalidate\', true); document.myform.reset(); return true;" title="' . html_attr($Translation['Print Preview']) . '"><i class="glyphicon glyphicon-print"></i> ' . $Translation['Print Preview'] . '</button>', $templateCode);
		if($AllowUpdate) {
			$templateCode = str_replace('<%%UPDATE_BUTTON%%>', '<button type="submit" class="btn btn-success btn-lg" id="update" name="update_x" value="1" onclick="return story_chrs_validateData();" title="' . html_attr($Translation['Save Changes']) . '"><i class="glyphicon glyphicon-ok"></i> ' . $Translation['Save Changes'] . '</button>', $templateCode);
		} else {
			$templateCode = str_replace('<%%UPDATE_BUTTON%%>', '', $templateCode);
		}
		if(($arrPerm[4]==1 && $ownerMemberID==getLoggedMemberID()) || ($arrPerm[4]==2 && $ownerGroupID==getLoggedGroupID()) || $arrPerm[4]==3) { // allow delete?
			$templateCode = str_replace('<%%DELETE_BUTTON%%>', '<button type="submit" class="btn btn-danger" id="delete" name="delete_x" value="1" onclick="return confirm(\'' . $Translation['are you sure?'] . '\');" title="' . html_attr($Translation['Delete']) . '"><i class="glyphicon glyphicon-trash"></i> ' . $Translation['Delete'] . '</button>', $templateCode);
		} else {
			$templateCode = str_replace('<%%DELETE_BUTTON%%>', '', $templateCode);
		}
		$templateCode = str_replace('<%%DESELECT_BUTTON%%>', '<button type="submit" class="btn btn-default" id="deselect" name="deselect_x" value="1" onclick="' . $backAction . '" title="' . html_attr($Translation['Back']) . '"><i class="glyphicon glyphicon-chevron-left"></i> ' . $Translation['Back'] . '</button>', $templateCode);
	} else {
		$templateCode = str_replace('<%%UPDATE_BUTTON%%>', '', $templateCode);
		$templateCode = str_replace('<%%DELETE_BUTTON%%>', '', $templateCode);
		$templateCode = str_replace('<%%DESELECT_BUTTON%%>', ($ShowCancel ? '<button type="submit" class="btn btn-default" id="deselect" name="deselect_x" value="1" onclick="' . $backAction . '" title="' . html_attr($Translation['Back']) . '"><i class="glyphicon glyphicon-chevron-left"></i> ' . $Translation['Back'] . '</button>' : ''), $templateCode);
	}

	// set records to read only if user can't insert new records and can't edit current record
	if(($selected_id && !$AllowUpdate && !$AllowInsert) || (!$selected_id && !$AllowInsert)) {
		$jsReadOnly .= "\tjQuery('#agent_id').prop('disabled', true).css({ color: '#555', backgroundColor: '#fff' });\n";
		$jsReadOnly .= "\tjQuery('#agent_id_caption').prop('disabled', true).css({ color: '#555', backgroundColor: 'white' });\n";
		$jsReadOnly .= "\tjQuery('#character').prop('disabled', true).css({ color: '#555', backgroundColor: '#fff' });\n";
		$jsReadOnly .= "\tjQuery('#character_caption').prop('disabled', true).css({ color: '#555', backgroundColor: 'white' });\n";
		$jsReadOnly .= "\tjQuery('#role').replaceWith('<div class=\"form-control-static\" id=\"role\">' + (jQuery('#role').val() || '') + '</div>');\n";
		$jsReadOnly .= "\tjQuery('#story_character').prop('disabled', true).css({ color: '#555', backgroundColor: '#fff' });\n";
		$jsReadOnly .= "\tjQuery('#story_character_caption').prop('disabled', true).css({ color: '#555', backgroundColor: 'white' });\n";
		$jsReadOnly .= "\tjQuery('#story').prop('disabled', true).css({ color: '#555', backgroundColor: '#fff' });\n";
		$jsReadOnly .= "\tjQuery('#story_caption').prop('disabled', true).css({ color: '#555', backgroundColor: 'white' });\n";
		$jsReadOnly .= "\tjQuery('#story_archetype').prop('disabled', true).css({ color: '#555', backgroundColor: '#fff' });\n";
		$jsReadOnly .= "\tjQuery('#story_archetype_caption').prop('disabled', true).css({ color: '#555', backgroundColor: 'white' });\n";
		$jsReadOnly .= "\tjQuery('#comment').replaceWith('<div class=\"form-control-static\" id=\"comment\">' + (jQuery('#comment').val() || '') + '</div>');\n";
		$jsReadOnly .= "\tjQuery('.select2-container').hide();\n";

		$noUploads = true;
	} elseif($AllowInsert) {
		$jsEditable .= "\tjQuery('form').eq(0).data('already_changed', true);"; // temporarily disable form change handler
			$jsEditable .= "\tjQuery('form').eq(0).data('already_changed', false);"; // re-enable form change handler
	}

	// process combos
	$templateCode = str_replace('<%%COMBO(agent_id)%%>', $combo_agent_id->HTML, $templateCode);
	$templateCode = str_replace('<%%COMBOTEXT(agent_id)%%>', $combo_agent_id->MatchText, $templateCode);
	$templateCode = str_replace('<%%URLCOMBOTEXT(agent_id)%%>', urlencode($combo_agent_id->MatchText), $templateCode);
	$templateCode = str_replace('<%%COMBO(character)%%>', $combo_character->HTML, $templateCode);
	$templateCode = str_replace('<%%COMBOTEXT(character)%%>', $combo_character->MatchText, $templateCode);
	$templateCode = str_replace('<%%URLCOMBOTEXT(character)%%>', urlencode($combo_character->MatchText), $templateCode);
	$templateCode = str_replace('<%%COMBO(story_character)%%>', $combo_story_character->HTML, $templateCode);
	$templateCode = str_replace('<%%COMBOTEXT(story_character)%%>', $combo_story_character->MatchText, $templateCode);
	$templateCode = str_replace('<%%URLCOMBOTEXT(story_character)%%>', urlencode($combo_story_character->MatchText), $templateCode);
	$templateCode = str_replace('<%%COMBO(story)%%>', $combo_story->HTML, $templateCode);
	$templateCode = str_replace('<%%COMBOTEXT(story)%%>', $combo_story->MatchText, $templateCode);
	$templateCode = str_replace('<%%URLCOMBOTEXT(story)%%>', urlencode($combo_story->MatchText), $templateCode);
	$templateCode = str_replace('<%%COMBO(story_archetype)%%>', $combo_story_archetype->HTML, $templateCode);
	$templateCode = str_replace('<%%COMBOTEXT(story_archetype)%%>', $combo_story_archetype->MatchText, $templateCode);
	$templateCode = str_replace('<%%URLCOMBOTEXT(story_archetype)%%>', urlencode($combo_story_archetype->MatchText), $templateCode);

	/* lookup fields array: 'lookup field name' => array('parent table name', 'lookup field caption') */
	$lookup_fields = array('agent_id' => array('biblio_author', 'Agent'), 'character' => array('bio_drama_chr_dev', 'Character'), 'story_character' => array('class_dramatica_character', 'Story character'), 'story' => array('hist_story', 'Story'), 'story_archetype' => array('class_dramatica_archetype', 'Story Archetype'), );
	foreach($lookup_fields as $luf => $ptfc) {
		$pt_perm = getTablePermissions($ptfc[0]);

		// process foreign key links
		if($pt_perm['view'] || $pt_perm['edit']) {
			$templateCode = str_replace("<%%PLINK({$luf})%%>", '<button type="button" class="btn btn-default view_parent hspacer-md" id="' . $ptfc[0] . '_view_parent" title="' . html_attr($Translation['View'] . ' ' . $ptfc[1]) . '"><i class="glyphicon glyphicon-eye-open"></i></button>', $templateCode);
		}

		// if user has insert permission to parent table of a lookup field, put an add new button
		if($pt_perm['insert'] && !$_REQUEST['Embedded']) {
			$templateCode = str_replace("<%%ADDNEW({$ptfc[0]})%%>", '<button type="button" class="btn btn-success add_new_parent hspacer-md" id="' . $ptfc[0] . '_add_new" title="' . html_attr($Translation['Add New'] . ' ' . $ptfc[1]) . '"><i class="glyphicon glyphicon-plus-sign"></i></button>', $templateCode);
		}
	}

	// process images
	$templateCode = str_replace('<%%UPLOADFILE(id)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(agent_id)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(character)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(role)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(story_character)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(story)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(story_archetype)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(comment)%%>', '', $templateCode);

	// process values
	if($selected_id) {
		if( $dvprint) $templateCode = str_replace('<%%VALUE(id)%%>', safe_html($urow['id']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(id)%%>', html_attr($row['id']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(id)%%>', urlencode($urow['id']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(agent_id)%%>', safe_html($urow['agent_id']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(agent_id)%%>', html_attr($row['agent_id']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(agent_id)%%>', urlencode($urow['agent_id']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(character)%%>', safe_html($urow['character']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(character)%%>', html_attr($row['character']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(character)%%>', urlencode($urow['character']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(role)%%>', safe_html($urow['role']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(role)%%>', html_attr($row['role']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(role)%%>', urlencode($urow['role']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(story_character)%%>', safe_html($urow['story_character']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(story_character)%%>', html_attr($row['story_character']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(story_character)%%>', urlencode($urow['story_character']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(story)%%>', safe_html($urow['story']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(story)%%>', html_attr($row['story']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(story)%%>', urlencode($urow['story']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(story_archetype)%%>', safe_html($urow['story_archetype']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(story_archetype)%%>', html_attr($row['story_archetype']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(story_archetype)%%>', urlencode($urow['story_archetype']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(comment)%%>', safe_html($urow['comment']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(comment)%%>', html_attr($row['comment']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(comment)%%>', urlencode($urow['comment']), $templateCode);
	} else {
		$templateCode = str_replace('<%%VALUE(id)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(id)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(agent_id)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(agent_id)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(character)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(character)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(role)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(role)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(story_character)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(story_character)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(story)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(story)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(story_archetype)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(story_archetype)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(comment)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(comment)%%>', urlencode(''), $templateCode);
	}

	// process translations
	$templateCode = parseTemplate($templateCode);

	// clear scrap
	$templateCode = str_replace('<%%', '<!-- ', $templateCode);
	$templateCode = str_replace('%%>', ' -->', $templateCode);

	// hide links to inaccessible tables
	if($_REQUEST['dvprint_x'] == '') {
		$templateCode .= "\n\n<script>\$j(function() {\n";
		$arrTables = getTableList();
		foreach($arrTables as $name => $caption) {
			$templateCode .= "\t\$j('#{$name}_link').removeClass('hidden');\n";
			$templateCode .= "\t\$j('#xs_{$name}_link').removeClass('hidden');\n";
		}

		$templateCode .= $jsReadOnly;
		$templateCode .= $jsEditable;

		if(!$selected_id) {
		}

		$templateCode.="\n});</script>\n";
	}

	// ajaxed auto-fill fields
	$templateCode .= '<script>';
	$templateCode .= '$j(function() {';

	$templateCode .= "\tagent_id_update_autofills$rnd1 = function() {\n";
	$templateCode .= "\t\t\$j.ajax({\n";
	if($dvprint) {
		$templateCode .= "\t\t\turl: 'story_chrs_autofill.php?rnd1=$rnd1&mfk=agent_id&id=' + encodeURIComponent('".addslashes($row['agent_id'])."'),\n";
		$templateCode .= "\t\t\tcontentType: 'application/x-www-form-urlencoded; charset=" . datalist_db_encoding . "',\n";
		$templateCode .= "\t\t\ttype: 'GET'\n";
	} else {
		$templateCode .= "\t\t\turl: 'story_chrs_autofill.php?rnd1=$rnd1&mfk=agent_id&id=' + encodeURIComponent(AppGini.current_agent_id{$rnd1}.value),\n";
		$templateCode .= "\t\t\tcontentType: 'application/x-www-form-urlencoded; charset=" . datalist_db_encoding . "',\n";
		$templateCode .= "\t\t\ttype: 'GET',\n";
		$templateCode .= "\t\t\tbeforeSend: function() { \$j('#agent_id$rnd1').prop('disabled', true); \$j('#agent_idLoading').html('<img src=loading.gif align=top>'); },\n";
		$templateCode .= "\t\t\tcomplete: function() { " . (($arrPerm[1] || (($arrPerm[3] == 1 && $ownerMemberID == getLoggedMemberID()) || ($arrPerm[3] == 2 && $ownerGroupID == getLoggedGroupID()) || $arrPerm[3] == 3)) ? "\$j('#agent_id$rnd1').prop('disabled', false); " : "\$j('#agent_id$rnd1').prop('disabled', true); ")."\$j('#agent_idLoading').html(''); \$j(window).resize(); }\n";
	}
	$templateCode .= "\t\t});\n";
	$templateCode .= "\t};\n";
	if(!$dvprint) $templateCode .= "\tif(\$j('#agent_id_caption').length) \$j('#agent_id_caption').click(function() { agent_id_update_autofills$rnd1(); });\n";


	$templateCode.="});";
	$templateCode.="</script>";
	$templateCode .= $lookups;

	// handle enforced parent values for read-only lookup fields

	// don't include blank images in lightbox gallery
	$templateCode = preg_replace('/blank.gif" data-lightbox=".*?"/', 'blank.gif"', $templateCode);

	// don't display empty email links
	$templateCode=preg_replace('/<a .*?href="mailto:".*?<\/a>/', '', $templateCode);

	/* default field values */
	$rdata = $jdata = get_defaults('story_chrs');
	if($selected_id) {
		$jdata = get_joined_record('story_chrs', $selected_id);
		if($jdata === false) $jdata = get_defaults('story_chrs');
		$rdata = $row;
	}
	$templateCode .= loadView('story_chrs-ajax-cache', array('rdata' => $rdata, 'jdata' => $jdata));

	// hook: story_chrs_dv
	if(function_exists('story_chrs_dv')) {
		$args=[];
		story_chrs_dv(($selected_id ? $selected_id : FALSE), getMemberInfo(), $templateCode, $args);
	}

	return $templateCode;
}