	$templateCode=preg_replace('/<a .*?href="mailto:".*?<\/a>/', '', $templateCode);

	/* default field values */
	$rdata = $jdata = get_defaults('storylines');
	if($selected_id){
		$jdata = get_joined_record('storylines', $selected_id);
		if($jdata === false) $jdata = get_defaults('storylines');
		$rdata = $row;
	}
	$templateCode .= loadView('storylines-ajax-cache', array('rdata' => $rdata, 'jdata' => $jdata));

	// hook: storylines_dv
	if(function_exists('storylines_dv')){
		$args=array();
		storylines_dv(($selected_id ? $selected_id : FALSE), getMemberInfo(), $templateCode, $args);
	}

	return $templateCode;
}
